package bd.edu.seu.customerdemo.controller;import bd.edu.seu.customerdemo.model.Customer;import bd.edu.seu.customerdemo.repository.CustomerRepository;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.time.temporal.ChronoUnit;import java.util.ArrayList;import java.util.List;@RestController@RequestMapping(value = "")public class CustomerController {    private CustomerRepository customerRepository;    public CustomerController(CustomerRepository customerRepository) {        this.customerRepository = customerRepository;    }    @GetMapping("")    public List<Customer> findAll(){        List<Customer> customerList = new ArrayList<>();         customerRepository.findAll().forEach(customerList::add);         return customerList;    }    @GetMapping("/{id}")    public Customer findById(@PathVariable Long id){            Customer customer = customerRepository.findById(id).orElse(new Customer());            return customer;    }}