package bd.edu.seu.customerdemo.service;import bd.edu.seu.customerdemo.model.Customer;import bd.edu.seu.customerdemo.model.Phone;import bd.edu.seu.customerdemo.repository.CustomerRepository;import bd.edu.seu.customerdemo.repository.PhoneRepository;import org.springframework.stereotype.Service;import java.lang.reflect.Array;import java.time.LocalDate;import java.util.Arrays;@Servicepublic class DummyDataService {    private CustomerRepository customerRepository;    private PhoneRepository phoneRepository;    public DummyDataService(CustomerRepository customerRepository, PhoneRepository phoneRepository) {        this.customerRepository = customerRepository;        this.phoneRepository = phoneRepository;    }    public void addDummyData(){        Phone phone1 = new Phone(1l,"32","+88","0168878787");        Phone phone2 = new Phone(2l,"32","+88","0170000000");        Phone phone3 = new Phone(3l,"32","+88","0190000000");        Phone phone4 = new Phone(4l,"32","+88","0140000000");        phoneRepository.save(phone1);        phoneRepository.save(phone2);        phoneRepository.save(phone3);        phoneRepository.save(phone4);        Customer customer1 = new Customer(1l, "Joniyed Bhuiyan",LocalDate.now(), Arrays.asList(phone1,phone2));        Customer customer2 = new Customer(2l, "Nahidul Islam",LocalDate.now(), Arrays.asList(phone3,phone4));        customerRepository.save(customer1);        customerRepository.save(customer2);    }}